import { signInWithPopup, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import { provider, auth } from "./Firebase";
import React, { useState } from 'react';


function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [hasAccount, setHasAccount] = useState(false);
    
    const clearInputs = () => {
      setEmail('');
      setPassword('');
    }
  
    const clearErrors = () => {
      setEmailError('');
      setPasswordError('');
    }
  
    const signInWithGoogle = () => {
      signInWithPopup(auth, provider);
    }
  
    const handleLogin = () => {
      clearErrors();
      signInWithEmailAndPassword(auth, email, password)
      .catch(err => {
        switch(err.code){
          case "auth/invalid-email":
            setEmailError("Your email and/or password do not match.");
            break;
          case "auth/user-disabled":
            setEmailError("Your account has been disabled.");
            break;
          case "auth/user-not-found":
            setEmailError(err.message);
            break;
          case "auth/wrong-password":
            setPasswordError("Your email and/or password do not match.");
            break;
        }
      });
    };

    const handleSignUp = () => {
      clearErrors();
      createUserWithEmailAndPassword(auth, email, password)
      .catch(err => {
        switch(err.code){
          case "auth/email-already-in-use":
            setEmailError("That email address is already in use, please use a different email address.");
            break;
          case "auth/invalid-email":
            setEmailError("Please enter a valid email address.");
            break;
          case "auth/weak-password":
            setPasswordError("The Password must be at least 6 characters Long.");
            break;
        }
      });
    };
  
    return(
      <div className="login-container">
        <div className="login-header">
          <img className="login-icon" src={process.env.PUBLIC_URL + 'logo300.png'}/>
          <h1 className="login-title">Sign in to Keryx</h1>
          <p className="login-text">Please enter your email<br/> and password</p>
        </div>
        <div className="login-input">
          <input className="email-input" type="text" autoFocus required value={email} onChange={e => setEmail(e.target.value)}/>
          <label className="email-label">Email address</label>
          <p className="login-error">{emailError}</p>
          <input className="password-input" type="password" required value={password} onChange={e => setPassword(e.target.value)}/>
          <label className="password-label">Password</label>
          <p className="login-error">{passwordError}</p>
        </div>
        <div className="login-footer">
              {hasAccount ? (
                  <>
                    <button onClick={handleSignUp}>Sign up</button>
                    <h2 className="login-or"><span>or</span></h2>
                    <div className="login-google" onClick={signInWithGoogle}>
                      <img className="login-google-icon" src="data:image/svg+xml;base64,"/>
                      <a className="login-google-btn">Sign up With Google</a>
                    </div>
                    <div className="login-have-account">
                      <p>have an account ? <span onClick={() => setHasAccount(!hasAccount)}>Sign in</span></p>
                    </div>
                  </>
              ) : (
                <>
                <button onClick={handleLogin}>Sign in</button>
                <h2 className="login-or"><span>or</span></h2>
                <div className="login-google" onClick={signInWithGoogle}>
                      <img className="login-google-icon" src="data:image/svg+xml;base64,"/>
                      <a className="login-google-btn">Sign in With Google</a>
                </div>
                <div className="login-have-account">
                  <p>Don't have an account ? <span onClick={() => setHasAccount(!hasAccount)}>Sign up</span></p>
                </div>
            </>
            )}
          </div>
        </div>
    )
};

export default Login;